package com.pan.bucket.utils;import java.util.ArrayList;import java.util.List;import org.json.JSONArray;import org.json.JSONException;import org.json.JSONObject;/** * @author : lipan * @create_time : 2014年11月11日 下午3:46:14 * @desc : Json工具类 * @update_person: * @update_time : * @update_desc : * */public class JsonUtils {    	/**	 * 字符串转json对象	 * @param json	 * @return	 */	public static JSONObject string2Json(String json)	{		try {			return new JSONObject(json);		} catch (JSONException e) {			e.printStackTrace();		}		return null;	}	    /**     * JsonArray 转 list；例如："data": ["百年讲堂", "第三教室", "4号楼", "第二教室"]     * @param array     * @return     */    public static List<String> jsonArray2List(JSONArray array)    {        List<String> list = new ArrayList<String>();        for (int i = 0; i < array.length(); i++)        {            list.add(StringB.nulltoBlank(array.opt(i)));        }        return list;    }}